import React, { useState } from "react";
import { Button, View, Text, Picker, StyleSheet } from "react-native";
import { DarkTheme, NavigationContainer } from "@react-navigation/native";
import { createNativeStackNavigator } from "@react-navigation/native-stack";

function HomeScreen({ navigation }) {
  return (
    <View style={{ flex: 1, alignItems: "center", justifyContent: "center" }}>
      <Text>Home Screen</Text>
      <Button
        title="Go to Details"
        onPress={() => navigation.navigate("Details")}
      />
    </View>
  );
}

function DetailsScreen() {
  const [selectedType, setselectedType] = useState("sfw");
  const [selectedCategory, setselectedCategory] = useState("waifu");

  const GetImage = () => {
    return fetch("https://reactnative.dev/movies.json")
      .then((response) => response.json())
      .then((json) => {
        return console.log(json.movies);
      })
      .catch((error) => {
        console.error(error);
      });
  };

  return (
    <View style={styles.container}>
      <Text>Tipo Imagem</Text>
      <Picker
        style={{ height: 50, width: 150 }}
        selectedValue={selectedType}
        onValueChange={(itemValue, itemIndex) => setselectedType(itemValue)}
      >
        <Picker.Item label="NSFW" value="nsfw" />
        <Picker.Item label="SFW" value="sfw" />
      </Picker>
      <Text>Categoria</Text>
      <Picker
        style={{ height: 50, width: 150 }}
        selectedValue={selectedCategory}
        onValueChange={(itemValue, itemIndex) => setselectedCategory(itemValue)}
      >
        <Picker.Item label="waifu" value="waifu" />
        <Picker.Item label="neko" value="neko" />
      </Picker>

      <Button title="Pegar Imagem" onPress={GetImage()} />
    </View>
  );
}

const Stack = createNativeStackNavigator();

function App() {
  return (
    <NavigationContainer>
      <Stack.Navigator initialRouteName="Home">
        <Stack.Screen name="Home" component={HomeScreen} />
        <Stack.Screen name="Details" component={DetailsScreen} />
      </Stack.Navigator>
    </NavigationContainer>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingTop: 40,
    alignItems: "center",
    backgroundColor: "#4287f5",
  },
});

export default App;
